import { useState } from "react";
import {
  TextField,
  Button,
  MenuItem,
  Container,
  Typography,
  Paper,
} from "@mui/material";
import { toast } from "react-toastify";
import { useNavigate } from "react-router-dom";

const Home = () => {
  const [subject, setSubject] = useState("");
  const [message, setMessage] = useState("");
  const [loading, setLoading] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");
  const navigate = useNavigate();

  const subjects = [
    "Bug Report",
    "Feature Recommendation",
    "General Inquiry",
    "Account Issue",
    "Become a Partner",
  ];

  const handleSearch = (e) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      navigate(`/nft?search=${encodeURIComponent(searchQuery)}`);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch(`${process.env.REACT_APP_BACKEND_BASEURL}/user/me`, {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
        },
        credentials: "include",
      });

      const data = await response.json();

      if (data.success && data.user) {
        setLoading(true);

        const res = await fetch(`${process.env.REACT_APP_BACKEND_BASEURL}/user/contact`, {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          credentials: "include",
          body: JSON.stringify({ subject, message }),
        });

        const result = await res.json();
        if (result.success) {
          toast.success(result.message || "Message sent successfully!");
          setSubject("");
          setMessage("");
        } else {
          toast.error(result.message || "Failed to send message.");
        }
      } else {
        toast.error("You must be logged in to send a message.");
      }
    } catch (error) {
      toast.error("Failed to check user authentication. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container maxWidth="lg">
      <Paper
        elevation={3}
        sx={{ padding: 5, textAlign: "center", marginBottom: 5 }}
      >
        <Typography variant="h3" gutterBottom>
          Welcome to NFT Marketplace
        </Typography>
        <Typography variant="h6" gutterBottom>
          Discover and trade unique NFTs
        </Typography>
        <TextField
          fullWidth
          variant="outlined"
          placeholder="Search for NFTs..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          onKeyDown={(e) => {
            if (e.key === "Enter") handleSearch(e);
          }}
          sx={{ marginTop: 2, bgcolor: "#fff", borderRadius: 1 }}
        />
      </Paper>

      <Paper elevation={3} sx={{ padding: 3, marginBottom: 5 }}>
        <Typography variant="h5" gutterBottom>
          Contact Us
        </Typography>
        <form onSubmit={handleSubmit}>
          <TextField
            select
            label="Subject"
            fullWidth
            value={subject}
            onChange={(e) => setSubject(e.target.value)}
            margin="normal"
            required
          >
            {subjects.map((subj) => (
              <MenuItem key={subj} value={subj}>
                {subj}
              </MenuItem>
            ))}
          </TextField>

          <TextField
            label="Message"
            fullWidth
            multiline
            rows={4}
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            margin="normal"
            required
          />

          <Button
            type="submit"
            variant="contained"
            color="primary"
            fullWidth
            disabled={loading}
            sx={{ marginTop: 2 }}
          >
            {loading ? "Sending..." : "Send Message"}
          </Button>
        </form>
      </Paper>
    </Container>
  );
};

export default Home;
